mapper.select:
SELECT * FROM usuario
WHERE correo = #{correo}
AND
password = #{password}
;

INSERT INTO usuario (nombre, apellidoPaterno, apellidoMaterno, telefono, direccion, fechaNacimiento)
VALUES (#{nombre}, #{apellidoPaterno}, #{apellidoMaterno}, #{telefono}, #{direccion}, #{fechaNacimiento})
WHERE correo = #{correo}
